import logo from "./logo.svg";
import "./App.css";
import React from "react";
import ReactDOM from "react-dom";

function Notification() {
  return (
    <div className="noti_container">
      <div className="noti_content1">
        <div className="my_img">
          <img src="" alt="image Oops Lost!" />
        </div>
        <div className="reaction_made">
          <div className="user_post">
            <p>
              <span className="name_user"></span>
              <span className="my_react"></span>
              <span className="topic"></span>
            </p>
            <div className="dot"></div>
          </div>
          <div className="universal_time">
            <p className="theTime"></p>
          </div>
        </div>
      </div>
      <div className="noti_content2">
        <div className="message">
          <p className="priv_message"></p>
        </div>
      </div>
    </div>
  );
}

class Notifications extends ReactDOM.Component {
  // constructor(myImg, myName, myMove, aboutPost, leftTime, privateMessage) {

  // }
  constructor(props) {
    super(props);
    this.state = {
      boardUsers: [],
    };
  }

  fillBoardUser() {
    this.setState({
      boardUsers: [
        {
          myImg: "",
          myName: "Mark Webber",
        },
        {},
        {},
        {},
        {},
        {},
        {},
      ],
    });
  }

  renderNotification(postItems) {
    return <Notification item={postItems} />;
  }

  render() {}
}

class showBoardNotification {
  render() {
    <div className="main_container">
      <div className="show_content">
        <div className="title_page">
          <div className="title">
            <h3>Notifications</h3>
            <div className="unread">
              <p className="remain_unreaded"></p>
            </div>
          </div>
          <div className="mark_noti">
            <button className="mark_all_read">Mark all as read</button>
          </div>
        </div>
        <Notifications />
      </div>
    </div>;
  }
}

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
